import { styled } from "styled-components"

const StyledModalWindow = styled.dialog`
  position: fixed;
  top: 0;
  left: 0;
  width: 60%;
  height: 60%;
  background-color: rgba(0, 0, 0, 0.5);
  transform: translate(-50%, -50%);
  // & div {
  //   position: absolute;
  //   top: 50%;
  //   left: 50%;
  //   transform: translate(-50%, -50%);
  // }
`

export default function ModalWindow({ children }) {
  return <StyledModalWindow open>{children}</StyledModalWindow>
}
