import { StyledToDoListItem } from "./StyledToDoListItem"
import { useState, useEffect } from "react"

export default function ToDoListItem({ object, onClick, ...props }) {
  const [completed, setCompleted] = useState(object.completed)

  // change completed status in local storage
  useEffect(() => {
    //get tasks from local storage
    const tasks = JSON.parse(localStorage.getItem("tasks"))
    if (tasks) {
      const task = tasks.find((task) => task.id === object.id)
      task.completed = completed
      console.log(`task`, task, `object`, object, `localstorage`, tasks)
      localStorage.setItem("tasks", JSON.stringify(tasks))
    }
  }, [completed])

  return (
    <StyledToDoListItem onClick={onClick}>
      <div className={`card ${completed ? "bg-success" : "bg-warning"}`}>
        <div className="card-body">
          <p>
            Task # {object.id} <span>{completed ? "Completed" : "To do"}</span>
          </p>
          <h3 className="card-title">{object.title}</h3>
        </div>
      </div>
    </StyledToDoListItem>
  )
}
