import Button from "../../Button/Button"
import { styled } from "styled-components"
import { useState } from "react"

const StyledNavBar = styled.nav`
  position: relative;
  //   flex-grow: 1;
  display: flex;
  flex-direction: column;
  & div {
    flex-grow: 1;
  }
  & button {
    width: 100%;
  }
`

export default function NavBar({ onActivePage }) {
  function setActiveTab(page) {
    onActivePage(page)
  }

  return (
    <StyledNavBar>
      <div
        className="d-flex flex-column flex-shrink-0 p-3 text-white bg-dark"
        style={{ width: "150px" }}
      >
        <hr></hr>

        <ul className="nav nav-pills flex-column mb-auto">
          <li>
            <Button
              className="btn btn-outline-light my-2"
              onClick={() => setActiveTab("Homework34")}
            >
              Homework34
              <hr className="my-0" />
              Vote for smile
            </Button>
          </li>

          <li>
            <Button className="btn btn-outline-light my-2">test button</Button>
          </li>

          <li>
            <Button className="btn btn-outline-light my-2">
              another test button
            </Button>
          </li>
        </ul>

        <hr></hr>
      </div>
    </StyledNavBar>
  )
}
