  display: flex;
  flex-direction: column;
  align-items: center;
  ul {
    padding: 0;
    margin: 0;
    // border: 1px solid black;
    display: flex;
    justify-content: center;
  }
  li {
    margin: 0 1rem;
    list-style: none;
    font-size: 3rem;
    // border: 1px solid black;
  }
  & .disabled {
  }
`

export default function VoteForSmile() {
  const [counters, setCounters] = useState({
    clown: 0,
    beast: 0,
    mask: 0,
    poop: 0,
    police: 0,
    winner: null,
  })
  const emojies = {
    clown: "ðŸ¤¡",
    beast: "ðŸ‘¹",
    mask: "ðŸ‘º",
    poop: "ðŸ’©",
    police: "ðŸ‘®",
  }

  function handleClick(event) {
    const emoji = event.currentTarget.getAttribute("emoji")
    setCounters((prev) => {
      return { ...prev, [emoji]: prev[emoji] + 1 }
    })
  }

  return (
    <StyledVoteForSmile>
      <h1 className="mt-3 mb-5">Vote for best emoji</h1>
      <ul>
        <Button a="12">ðŸ¤¡</Button>
      </ul>
    </StyledVoteForSmile>
  )
}
